var mobileNav={mainNav:function(){var e=document.querySelector(".burger"),t=document.querySelector("#"+e.dataset.target);e.addEventListener("click",function(){e.classList.toggle("is-active"),t.classList.toggle("is-active")})}};function findYOffset(){var e;40<=window.pageYOffset?hideNavBar():showNavBar()}function showNavBar(){var e;document.getElementById("nav").classList.remove("is-hidden")}function hideNavBar(){var e;document.getElementById("nav").classList.add("is-hidden")}window.addEventListener("scroll",function(){findYOffset()}),document.addEventListener("DOMContentLoaded",function(){mobileNav.mainNav()});